    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/core/GasSubsidy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract GasSubsidy is Ownable, ReentrancyGuard {
    // === 状态变量 ===

    // 每次补贴的 Gas 金额 (例如 1 ORA)
    uint256 public subsidyAmount = 1 ether;

    // 记录已领取的地址 (防止重复领取)
    mapping(address => bool) public hasClaimed;

    // 授权的中继器地址 (你的服务器钱包)
    address public relayerAddress;

    // === 事件 ===
    event GasSubsidized(address indexed user, uint256 amount);
    event RelayerUpdated(address indexed newRelayer);
    event SubsidyAmountUpdated(uint256 newAmount);

    // === 构造函数 ===
    constructor(address _initialOwner, address _relayer) Ownable(_initialOwner) {
        relayerAddress = _relayer;
    }

    // === 修饰符 ===
    modifier onlyRelayerOrOwner() {
        require(msg.sender == relayerAddress || msg.sender == owner(), "Not authorized");
        _;
    }

    // === 核心功能 ===

    /**
     * @dev 给指定用户发放 Gas 补贴
     * @param _user 目标新用户地址
     * 注意：只有服务器(Relayer)能调用此函数，这样我们可以在链下做 IP 验证和防刷
     */
    function subsidize(address _user) external nonReentrant onlyRelayerOrOwner {
        require(_user != address(0), "Invalid address");
        require(!hasClaimed[_user], "User already claimed subsidy");
        require(address(this).balance >= subsidyAmount, "Insufficient subsidy funds");

        // 1. 标记为已领取
        hasClaimed[_user] = true;

        // 2. 发送原生代币 (ORA)
        (bool sent, ) = _user.call{value: subsidyAmount}("");
        require(sent, "Failed to send Ether");

        emit GasSubsidized(_user, subsidyAmount);
    }

    /**
     * @dev 批量发放 (用于活动或空投)
     */
    function batchSubsidize(address[] calldata _users) external onlyRelayerOrOwner {
        for (uint i = 0; i < _users.length; i++) {
            if (!hasClaimed[_users[i]] && address(this).balance >= subsidyAmount) {
                hasClaimed[_users[i]] = true;
                (bool sent, ) = payable(_users[i]).call{value: subsidyAmount}("");
                require(sent, "Failed to send Ether");
                emit GasSubsidized(_users[i], subsidyAmount);
            }
        }
    }

    // === 管理功能 ===

    // 协议金库向此合约充值
    receive() external payable {}

    function setRelayer(address _newRelayer) external onlyOwner {
        relayerAddress = _newRelayer;
        emit RelayerUpdated(_newRelayer);
    }

    function setSubsidyAmount(uint256 _amount) external onlyOwner {
        subsidyAmount = _amount;
        emit SubsidyAmountUpdated(_amount);
    }

    // 紧急提现 (防止资金锁死)
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        payable(owner()).transfer(amount);
    }
}

contracts/FaucetUpgradeable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title SimpleFaucetUpgradeable
 * @dev 简化版水龙头合约
 */
contract FaucetUpgradeable is Ownable {
    IERC20 public oraToken;

    // 每次领取的代币数量 (可修改)
    uint256 public withdrawAmount;

    // 每个地址的冷却时间 (可修改)
    uint256 public cooldownPeriod;

    // 记录每个地址的最后领取时间
    mapping(address => uint256) public lastWithdrawal;

    // 事件
    event TokensWithdrawn(address indexed recipient, uint256 amount);
    event TokensDeposited(address indexed sender, uint256 amount);
    event WithdrawalLimitChanged(uint256 newLimit);
    event CooldownPeriodChanged(uint256 newPeriod);

    /**
     * @dev 构造函数
     * @param _oraToken ORA代币合约地址
     */
    constructor(address _oraToken) Ownable(msg.sender) {
        require(_oraToken != address(0), "Token address cannot be zero");
        oraToken = IERC20(_oraToken);

        // 设置默认值
        withdrawAmount = 1000 * 10**18; // 1000 ORA
        cooldownPeriod = 1 days; // 24小时
    }

    /**
     * @dev 领取代币
     */
    function withdrawTokens() external {
        require(
            lastWithdrawal[msg.sender] + cooldownPeriod <= block.timestamp,
            "Must wait for cooldown period"
        );

        require(
            oraToken.balanceOf(address(this)) >= withdrawAmount,
            "Insufficient faucet balance"
        );

        lastWithdrawal[msg.sender] = block.timestamp;

        bool success = oraToken.transfer(msg.sender, withdrawAmount);
        require(success, "Transfer failed");

        emit TokensWithdrawn(msg.sender, withdrawAmount);
    }

    /**
     * @dev 批量向多个地址分发代币（仅限所有者）
     * @param recipients 接收者地址数组
     */
    function batchDistribute(address[] calldata recipients) external onlyOwner {
        uint256 totalAmount = withdrawAmount * recipients.length;

        require(
            oraToken.balanceOf(address(this)) >= totalAmount,
            "Insufficient faucet balance"
        );

        for (uint256 i = 0; i < recipients.length; i++) {
            address recipient = recipients[i];
            require(
                lastWithdrawal[recipient] + cooldownPeriod <= block.timestamp,
                "Recipient must wait for cooldown period"
            );

            lastWithdrawal[recipient] = block.timestamp;

            bool success = oraToken.transfer(recipient, withdrawAmount);
            require(success, "Transfer failed");

            emit TokensWithdrawn(recipient, withdrawAmount);
        }
    }

    /**
     * @dev 向水龙头存入代币
     * @param amount 存入数量
     */
    function depositTokens(uint256 amount) external onlyOwner {
        bool success = oraToken.transferFrom(msg.sender, address(this), amount);
        require(success, "Deposit failed");

        emit TokensDeposited(msg.sender, amount);
    }

    /**
     * @dev 从水龙头提取代币（仅限所有者）
     * @param amount 提取数量
     */
    function withdrawFaucetBalance(uint256 amount) external onlyOwner {
        bool success = oraToken.transfer(msg.sender, amount);
        require(success, "Withdrawal failed");
    }

    /**
     * @dev 检查某个地址是否可以领取代币
     * @param account 账户地址
     */
    function canWithdraw(address account) external view returns (bool) {
        return lastWithdrawal[account] + cooldownPeriod <= block.timestamp;
    }

    /**
     * @dev 获取账户还需等待的时间
     * @param account 账户地址
     */
    function timeToNextWithdraw(address account) external view returns (uint256) {
        if (lastWithdrawal[account] + cooldownPeriod <= block.timestamp) {
            return 0;
        }
        return lastWithdrawal[account] + cooldownPeriod - block.timestamp;
    }

    /**
     * @dev 更新每次领取的代币数量（仅限所有者）
     * @param newAmount 新的领取数量
     */
    function setWithdrawAmount(uint256 newAmount) external onlyOwner {
        require(newAmount > 0, "Amount must be greater than 0");
        uint256 oldAmount = withdrawAmount;
        withdrawAmount = newAmount;
        emit WithdrawalLimitChanged(newAmount);
    }

    /**
     * @dev 更新冷却时间（仅限所有者）
     * @param newPeriod 新的冷却时间
     */
    function setCooldownPeriod(uint256 newPeriod) external onlyOwner {
        cooldownPeriod = newPeriod;
        emit CooldownPeriodChanged(newPeriod);
    }

    /**
     * @dev 获取水龙头余额
     */
    function faucetBalance() external view returns (uint256) {
        return oraToken.balanceOf(address(this));
    }

    /**
     * @dev Fallback function to receive ETH if needed
     */
    receive() external payable {}

    /**
     * @dev Withdraw ETH from the contract (if any)
     */
    function withdrawETH() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }
}

contracts/IPRiskCalculator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title IP 风险计算器
 * @notice 纯逻辑合约，定义风险评分算法
 */
contract IPRiskCalculator {

    enum AttackType {
        UNKNOWN,
        BRUTE_FORCE,  // 暴力破解
        DDOS,         // 拒绝服务
        MALWARE,      // 恶意软件传播
        SCANNING,     // 端口扫描
        PHISHING      // 钓鱼节点
    }

    // 基础分数表
    mapping(AttackType => uint256) public baseScores;

    constructor() {
        // 初始化评分标准 (可以由治理合约修改)
        baseScores[AttackType.BRUTE_FORCE] = 20;
        baseScores[AttackType.DDOS] = 50;
        baseScores[AttackType.MALWARE] = 80;
        baseScores[AttackType.SCANNING] = 10;
        baseScores[AttackType.PHISHING] = 60;
    }

    /**
     * @notice 计算单次攻击后的风险分
     * @param currentScore 当前分数
     * @param attackType 攻击类型
     * @param frequency 24小时内攻击次数
     * @return newScore 新的风险分
     */
    function calculateRisk(
        uint256 currentScore, 
        uint8 attackType, 
        uint256 frequency
    ) external view returns (uint256 newScore) {
        uint256 base = baseScores[AttackType(attackType)];
        if (base == 0) base = 10; // 默认分

        // 算法：新分数 = 旧分数 + (基础分 * 频率加成)
        // 频率越高，加成越狠
        uint256 multiplier = 1;
        if (frequency > 10) multiplier = 2;
        if (frequency > 100) multiplier = 5;

        newScore = currentScore + (base * multiplier);

        // 封顶 1000 分
        if (newScore > 1000) return 1000;
        return newScore;
    }

    /**
     * @notice 风险等级评估
     * @return level 0:安全, 1:可疑, 2:高危, 3:极度危险
     */
    function evaluateRiskLevel(uint256 score) external pure returns (uint8 level) {
        if (score < 50) return 0;
        if (score < 200) return 1;
        if (score < 600) return 2;
        return 3;
    }

    /**
     * @notice 更新基础评分标准
     * @param attackType 攻击类型
     * @param newScore 新的评分
     */
    function updateBaseScore(AttackType attackType, uint256 newScore) external {
        require(newScore <= 100, "Score cannot exceed 100");
        baseScores[attackType] = newScore;
    }
}

