// contracts/mocks/MockERC20.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockERC20 is ERC20 {
    uint8 private immutable _decimals;

    constructor(
        string memory name,
        string memory symbol,
        uint8 decimals_
    ) ERC20(name, symbol) {
        _decimals = decimals_;
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) public {
        _mint(to, amount);
    }

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) public virtual override returns (bool) {
        // 允许任何账户转移代币，用于测试目的
        address spender = msg.sender;
        _spendAllowance(from, spender, value);
        _transfer(from, to, value);
        return true;
    }
}

contracts/NodeRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract NodeRegistry is Ownable {
    struct NodeInfo {
        string ip;
        uint16 port;
        address wallet;
        uint256 registeredAt;
        bool active;
    }

    NodeInfo[] public activeNodes;
    mapping(address => uint256) public nodeIndex; // 钱包地址到节点索引的映射
    mapping(string => bool) public ipRegistered; // IP是否已注册

    event NodeRegistered(address indexed wallet, string ip, uint16 port);
    event NodeDeactivated(address indexed wallet, string ip);
    event NodeReactivated(address indexed wallet, string ip);

    constructor() Ownable(msg.sender) {}

    /**
     * @dev 注册节点
     * @param _ip 节点IP地址
     * @param _port 节点端口
     */
    function registerNode(string memory _ip, uint16 _port) external {
        require(bytes(_ip).length > 0, "IP cannot be empty");
        require(_port > 0, "Port must be greater than 0");
        require(!ipRegistered[_ip], "IP already registered");

        // 检查是否已注册
        if (nodeIndex[msg.sender] == 0) {
            // 新节点注册
            activeNodes.push(NodeInfo({
                ip: _ip,
                port: _port,
                wallet: msg.sender,
                registeredAt: block.timestamp,
                active: true
            }));

            uint256 nodeIdx = activeNodes.length - 1;
            nodeIndex[msg.sender] = nodeIdx + 1; // +1 因为 0 表示未注册
            ipRegistered[_ip] = true;

            emit NodeRegistered(msg.sender, _ip, _port);
        } else {
            // 重新激活已注册的节点
            uint256 nodeIdx = nodeIndex[msg.sender] - 1;
            NodeInfo storage node = activeNodes[nodeIdx];

            require(!node.active, "Node already active");

            node.ip = _ip;
            node.port = _port;
            node.active = true;
            ipRegistered[_ip] = true;

            emit NodeReactivated(msg.sender, _ip);
        }
    }

    /**
     * @dev 注销节点
     */
    function unregisterNode() external {
        require(nodeIndex[msg.sender] != 0, "Node not registered");

        uint256 nodeIdx = nodeIndex[msg.sender] - 1;
        NodeInfo storage node = activeNodes[nodeIdx];

        require(node.active, "Node not active");

        node.active = false;
        ipRegistered[node.ip] = false;

        emit NodeDeactivated(msg.sender, node.ip);
    }

    /**
     * @dev 获取所有活跃节点
     */
    function getActiveNodes() external view returns (NodeInfo[] memory) {
        NodeInfo[] memory result = new NodeInfo[](0);
        uint256 count = 0;

        // 先计算活跃节点数量
        for (uint256 i = 0; i < activeNodes.length; i++) {
            if (activeNodes[i].active) {
                count++;
            }
        }

        // 创建正确大小的数组
        result = new NodeInfo[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < activeNodes.length; i++) {
            if (activeNodes[i].active) {
                result[index] = activeNodes[i];
                index++;
            }
        }

        return result;
    }

    /**
     * @dev 获取活跃节点数量
     */
    function getActiveNodeCount() external view returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < activeNodes.length; i++) {
            if (activeNodes[i].active) {
                count++;
            }
        }
        return count;
    }

    /**
     * @dev 治理功能：强制注销节点
     * @param _wallet 要注销的钱包地址
     */
    function forceUnregisterNode(address _wallet) external onlyOwner {
        require(nodeIndex[_wallet] != 0, "Node not registered");

        uint256 nodeIdx = nodeIndex[_wallet] - 1;
        NodeInfo storage node = activeNodes[nodeIdx];

        node.active = false;
        ipRegistered[node.ip] = false;

        emit NodeDeactivated(_wallet, node.ip);
    }

    /**
     * @dev 检查节点是否已注册并活跃
     */
    function isNodeActive(address _wallet) external view returns (bool) {
        if (nodeIndex[_wallet] == 0) {
            return false;
        }

        uint256 nodeIdx = nodeIndex[_wallet] - 1;
        return activeNodes[nodeIdx].active;
    }
}

contracts/OraSRSGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title OraSRSGovernance
 * @dev OraSRS协议治理合约，用于管理协议参数和关键决策
 */
contract OraSRSGovernance is Ownable, ReentrancyGuard {
    // 提案状态
    enum ProposalState { 
        Pending,     // 待处理
        Active,      // 激活（正在投票）
        Canceled,    // 已取消
        Defeated,    // 已否决
        Succeeded,   // 已通过
        Queued,      // 已排队
        Executed     // 已执行
    }

    // 提案类型
    enum ProposalType {
        ParameterUpdate,     // 参数更新
        ContractUpgrade,     // 合约升级
        EmergencyAction,     // 紧急操作
        NodeManagement,      // 节点管理
        ThreatIntelSync,     // 威胁情报同步
        TreasuryManagement   // 财政管理
    }

    // 提案结构
    struct Proposal {
        uint256 id;
        address proposer;
        uint256 startTime;
        uint256 endTime;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 votesAbstain;
        uint256 requiredQuorum;
        string description;
        ProposalType proposalType;
        ProposalState state;
        address[] targets;
        uint256[] values;
        bytes[] calldatas;
        mapping(address => bool) hasVoted;
    }

    // 投票结构
    struct Vote {
        bool hasVoted;
        uint8 support;  // 0=Against, 1=For, 2=Abstain
        uint256 votes;
    }

    // 存储变量
    uint256 public proposalCount;
    mapping(uint256 => Proposal) public proposals;
    mapping(uint256 => mapping(address => Vote)) public votes;

    // 治理相关地址
    address public timelock;
    address public threatIntelligenceCoordination;

    // 治理参数
    uint256 public votingPeriod;  // 投票期（秒）
    uint256 public proposalThreshold;  // 提案门槛（代币数量）
    uint256 public quorumPercentage;   // 法定人数百分比（百万分之一）

    // 事件
    event ProposalCreated(
        uint256 indexed id,
        address indexed proposer,
        string description,
        ProposalType indexed proposalType
    );
    event VoteCast(
        uint256 indexed proposalId,
        address indexed voter,
        uint8 support,
        uint256 votes
    );
    event ProposalExecuted(uint256 indexed id);
    event GovernanceParameterUpdated(string parameter, uint256 value);
    event GovernanceAddressUpdated(string contractName, address oldAddress, address newAddress);

    /**
     * @dev 构造函数
     * @param _timelock timelock合约地址
     * @param _threatIntelligenceCoordination 威胁情报协调合约地址
     */
    constructor(address _timelock, address _threatIntelligenceCoordination) Ownable(msg.sender) {
        timelock = _timelock;
        threatIntelligenceCoordination = _threatIntelligenceCoordination;

        // 设置默认治理参数
        votingPeriod = 7 days;  // 7天投票期
        proposalThreshold = 10000 * 10**18;  // 10,000 ORA代币门槛
        quorumPercentage = 100000;  // 10% 法定人数（百万分之一）
    }

    /**
     * @dev 创建提案
     */
    function createProposal(
        string memory description,
        ProposalType proposalType,
        address[] memory targets,
        uint256[] memory values,
        bytes[] memory calldatas
    ) external returns (uint256) {
        // TODO: 检查提案者是否有足够投票权
        require(bytes(description).length > 0, "Description cannot be empty");
        require(targets.length == values.length && targets.length == calldatas.length, 
                "Array lengths must match");

        uint256 newProposalId = ++proposalCount;

        Proposal storage newProposal = proposals[newProposalId];
        newProposal.id = newProposalId;
        newProposal.proposer = msg.sender;
        newProposal.startTime = block.timestamp;
        newProposal.endTime = block.timestamp + votingPeriod;
        newProposal.votesFor = 0;
        newProposal.votesAgainst = 0;
        newProposal.votesAbstain = 0;
        newProposal.requiredQuorum = quorumPercentage;
        newProposal.description = description;
        newProposal.proposalType = proposalType;
        newProposal.state = ProposalState.Pending;
        newProposal.targets = targets;
        newProposal.values = values;
        newProposal.calldatas = calldatas;

        // 根据提案类型设置初始状态
        newProposal.state = ProposalState.Active;

        emit ProposalCreated(newProposalId, msg.sender, description, proposalType);

        return newProposalId;
    }

    /**
     * @dev 投票
     */
    function castVote(uint256 proposalId, uint8 support) external nonReentrant {
        require(proposalId > 0 && proposalId <= proposalCount, "Invalid proposal id");
        require(support <= 2, "Invalid vote support value");

        Proposal storage proposal = proposals[proposalId];
        require(proposal.state == ProposalState.Active, "Proposal is not active");
        require(block.timestamp <= proposal.endTime, "Voting period has ended");
        require(!proposal.hasVoted[msg.sender], "Already voted");

        // 标记已投票
        proposal.hasVoted[msg.sender] = true;

        // 记录投票（简化：使用固定权重）
        Vote storage vote = votes[proposalId][msg.sender];
        vote.hasVoted = true;
        vote.support = support;
        vote.votes = 100; // 简化：固定票数

        // 更新计票
        if (support == 1) {
            proposal.votesFor += 100;
        } else if (support == 0) {
            proposal.votesAgainst += 100;
        } else if (support == 2) {
            proposal.votesAbstain += 100;
        }

        emit VoteCast(proposalId, msg.sender, support, 100);
    }

    /**
     * @dev 获取提案详情
     */
    function getProposalDetails(uint256 proposalId) 
        external 
        view 
        returns (
            address,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256,
            string memory,
            ProposalType,
            ProposalState
        ) 
    {
        Proposal storage proposal = proposals[proposalId];
        return (
            proposal.proposer,
            proposal.startTime,
            proposal.endTime,
            proposal.votesFor,
            proposal.votesAgainst,
            proposal.votesAbstain,
            proposal.requiredQuorum,
            proposal.description,
            proposal.proposalType,
            proposal.state
        );
    }

    /**
     * @dev 获取提案状态
     */
    function state(uint256 proposalId) external view returns (ProposalState) {
        require(proposalId > 0 && proposalId <= proposalCount, "Invalid proposal id");

        Proposal storage proposal = proposals[proposalId];

        if (proposal.endTime < block.timestamp) {
            // 检查是否通过
            if (proposal.votesFor > proposal.votesAgainst && 
                (proposal.votesFor + proposal.votesAgainst) >= proposal.requiredQuorum) {
                return ProposalState.Succeeded;
            } else {
                return ProposalState.Defeated;
            }
        }

        return proposal.state;
    }

    /**
     * @dev 更新timelock合约地址
     */
    function updateTimelock(address _newTimelock) external onlyOwner {
        require(_newTimelock != address(0), "Invalid timelock address");
        address oldTimelock = timelock;
        timelock = _newTimelock;
        emit GovernanceAddressUpdated("Timelock", oldTimelock, _newTimelock);
    }

    /**
     * @dev 更新威胁情报协调合约地址
     */
    function updateThreatIntelligenceCoordination(address _newContract) external onlyOwner {
        require(_newContract != address(0), "Invalid contract address");
        address oldContract = threatIntelligenceCoordination;
        threatIntelligenceCoordination = _newContract;
        emit GovernanceAddressUpdated("ThreatIntelligenceCoordination", oldContract, _newContract);
    }

    /**
     * @dev 更新投票期
     */
    function updateVotingPeriod(uint256 _newVotingPeriod) external onlyOwner {
        require(_newVotingPeriod > 0, "Voting period must be greater than 0");
        votingPeriod = _newVotingPeriod;
        emit GovernanceParameterUpdated("VotingPeriod", _newVotingPeriod);
    }

    /**
     * @dev 更新提案门槛
     */
    function updateProposalThreshold(uint256 _newThreshold) external onlyOwner {
        proposalThreshold = _newThreshold;
        emit GovernanceParameterUpdated("ProposalThreshold", _newThreshold);
    }

    /**
     * @dev 更新法定人数百分比
     */
    function updateQuorumPercentage(uint256 _newQuorumPercentage) external onlyOwner {
        require(_newQuorumPercentage <= 1000000, "Quorum cannot exceed 100%");
        quorumPercentage = _newQuorumPercentage;
        emit GovernanceParameterUpdated("QuorumPercentage", _newQuorumPercentage);
    }

    /**
     * @dev 销毁合约（仅所有者）
     * 这将把合约余额发送到指定地址并删除合约
     */
    function destroy() external onlyOwner {
        emit ProposalExecuted(block.timestamp); // 记录最后操作
        selfdestruct(payable(owner()));
    }

    /**
     * @dev 销毁合约并发送余额到指定地址（仅所有者）
     */
    function destroyAndSendTo(address payable _recipient) external onlyOwner {
        require(_recipient != address(0), "Invalid recipient address");
        emit ProposalExecuted(block.timestamp); // 记录最后操作
        selfdestruct(_recipient);
    }
}

contracts/OraSRSReader.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IThreatCoordination {
    function getThreatScore(string memory ip) external view returns (uint256);
    function isThreatSource(string memory ip) external view returns (bool);
    function getThreatIntel(string memory ip) external view returns (
        string memory sourceIP,
        string memory targetIP,
        uint8 threatLevel, // 使用uint8代替enum，因为interface中enum可能有问题
        uint256 timestamp,
        string memory threatType,
        bool isActive
    );
}

interface IRiskCalculator {
    function evaluateRiskLevel(uint256 score) external pure returns (uint8);
}

/**
 * @title OraSRSReader - 高效批量查询合约
 * @notice 为客户端（防火墙）提供批量查询功能
 */
contract OraSRSReader {

    IThreatCoordination public storageContract;
    IRiskCalculator public calculatorContract;

    constructor(address _storage, address _calc) {
        storageContract = IThreatCoordination(_storage);
        calculatorContract = IRiskCalculator(_calc);
    }

    struct BulkResult {
        string ip;
        uint256 score;
        uint8 riskLevel; // 0-3
        bool shouldBlock;
    }

    struct ThreatInfo {
        string ip;
        uint256 score;
        uint8 riskLevel;
        uint8 threatLevel; // 0-3 (Info, Warning, Critical, Emergency)
        uint256 timestamp;
        string threatType;
        bool isActive;
    }

    /**
     * @notice 批量查询接口：防火墙每分钟调用一次
     * @param ips 要查询的 IP 列表
     * @param threshold 封禁阈值 (例如 80 分)
     * @return 批量查询结果
     */
    function checkMultipleIPs(string[] calldata ips, uint256 threshold) 
        external 
        view 
        returns (BulkResult[] memory) 
    {
        BulkResult[] memory results = new BulkResult[](ips.length);

        for (uint i = 0; i < ips.length; i++) {
            // 1. 从存储合约读分
            uint256 score = storageContract.getThreatScore(ips[i]);

            // 2. 从计算合约读等级
            uint8 level = calculatorContract.evaluateRiskLevel(score);

            // 3. 决定是否建议拦截
            bool blockIt = score >= threshold;

            results[i] = BulkResult(ips[i], score, level, blockIt);
        }

        return results;
    }

    /**
     * @notice 获取单个IP的风险信息
     * @param ip 要查询的IP
     * @param threshold 封禁阈值
     * @return ipResult IP地址
     * @return score 风险分数
     * @return riskLevel 风险等级
     * @return shouldBlock 是否应该拦截
     */
    function checkSingleIP(string memory ip, uint256 threshold) 
        external 
        view 
        returns (
            string memory ipResult,
            uint256 score,
            uint8 riskLevel,
            bool shouldBlock
        ) 
    {
        uint256 score = storageContract.getThreatScore(ip);
        uint8 level = calculatorContract.evaluateRiskLevel(score);
        bool shouldBlock = score >= threshold;

        return (ip, score, level, shouldBlock);
    }

    /**
     * @notice 批量查询并返回过滤后的结果（仅返回高风险IP）
     * @param ips 要查询的 IP 列表
     * @param threshold 封禁阈值
     * @return 过滤后的高风险IP结果
     */
    function checkMultipleIPsFiltered(string[] calldata ips, uint256 threshold) 
        external 
        view 
        returns (BulkResult[] memory) 
    {
        // 首先计算总数
        uint256 highRiskCount = 0;
        for (uint i = 0; i < ips.length; i++) {
            uint256 score = storageContract.getThreatScore(ips[i]);
            if (score >= threshold) {
                highRiskCount++;
            }
        }

        // 创建结果数组
        BulkResult[] memory results = new BulkResult[](highRiskCount);
        uint256 index = 0;

        for (uint i = 0; i < ips.length; i++) {
            uint256 score = storageContract.getThreatScore(ips[i]);
            uint8 level = calculatorContract.evaluateRiskLevel(score);
            bool blockIt = score >= threshold;

            if (blockIt) {
                results[index] = BulkResult(ips[i], score, level, blockIt);
                index++;
            }
        }

        return results;
    }

    /**
     * @notice 获取威胁IP列表
     * @param ips 要检查的IP列表
     * @return 威胁IP信息数组
     */
    function getThreatIPs(string[] calldata ips) 
        external 
        view 
        returns (ThreatInfo[] memory) 
    {
        ThreatInfo[] memory threatIPs = new ThreatInfo[](ips.length);
        uint count = 0;

        for (uint i = 0; i < ips.length; i++) {
            bool isThreat = storageContract.isThreatSource(ips[i]);
            if (isThreat) {
                (, , uint8 tLevel, uint256 timestamp, string memory tType, bool isActive) = 
                    storageContract.getThreatIntel(ips[i]);

                uint256 score = storageContract.getThreatScore(ips[i]);
                uint8 riskLevel = calculatorContract.evaluateRiskLevel(score);

                threatIPs[count] = ThreatInfo({
                    ip: ips[i],
                    score: score,
                    riskLevel: riskLevel,
                    threatLevel: tLevel,
                    timestamp: timestamp,
                    threatType: tType,
                    isActive: isActive
                });

                count++;
            }
        }

        // 调整数组大小以仅包含实际的威胁IP
        ThreatInfo[] memory result = new ThreatInfo[](count);
        for (uint i = 0; i < count; i++) {
            result[i] = threatIPs[i];
        }

        return result;
    }

    /**
     * @notice 获取所有威胁IP的详细信息
     * @param ips 要检查的IP列表
     * @return 所有IP的威胁信息数组
     */
    function getAllThreatInfo(string[] calldata ips) 
        external 
        view 
        returns (ThreatInfo[] memory) 
    {
        ThreatInfo[] memory results = new ThreatInfo[](ips.length);

        for (uint i = 0; i < ips.length; i++) {
            bool isThreat = storageContract.isThreatSource(ips[i]);
            uint256 score = storageContract.getThreatScore(ips[i]);
            uint8 riskLevel = calculatorContract.evaluateRiskLevel(score);

            if (isThreat) {
                (, , uint8 tLevel, uint256 timestamp, string memory tType, bool isActive) = 
                    storageContract.getThreatIntel(ips[i]);

                results[i] = ThreatInfo({
                    ip: ips[i],
                    score: score,
                    riskLevel: riskLevel,
                    threatLevel: tLevel,
                    timestamp: timestamp,
                    threatType: tType,
                    isActive: isActive
                });
            } else {
                results[i] = ThreatInfo({
                    ip: ips[i],
                    score: score,
                    riskLevel: riskLevel,
                    threatLevel: 0,
                    timestamp: 0,
                    threatType: "",
                    isActive: false
                });
            }
        }

        return results;
    }

    /**
     * @notice 更新存储合约地址
     * @param newStorage 新的存储合约地址
     */
    function updateStorageContract(address newStorage) external {
        require(newStorage != address(0), "Invalid address");
        storageContract = IThreatCoordination(newStorage);
    }

    /**
     * @notice 更新风险计算器合约地址
     * @param newCalc 新的风险计算器合约地址
     */
    function updateCalculatorContract(address newCalc) external {
        require(newCalc != address(0), "Invalid address");
        calculatorContract = IRiskCalculator(newCalc);
    }
}

