// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";

/**
 * @title OraSRSToken
 * @dev OraSRS Protocol 代币合约
 * @notice 这是 OraSRS 协议的治理代币
 */
contract OraSRSToken is ERC20, Ownable, ERC20Burnable {
    uint8 private constant _DECIMALS = 18;
    uint256 private constant _INITIAL_SUPPLY = 100_000_000 * 10 ** _DECIMALS; // 1亿枚代币

    /**
     * @dev 构造函数
     * @notice 初始化代币名称、符号和初始供应量
     */
    constructor() ERC20("OraSRS Protocol Token", "ORA") Ownable(msg.sender) {
        _mint(msg.sender, _INITIAL_SUPPLY);
    }

    /**
     * @dev 铸造新代币
     * @param to 接收地址
     * @param amount 铸造数量
     */
    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev 返回代币精度
     */
    function decimals() public pure override returns (uint8) {
        return _DECIMALS;
    }
}

contracts/SimpleSecurityActionContract.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SimpleSecurityActionContract
 * @dev 简化的安全操作合约，用于管理IP和域名阻断
 */
contract SimpleSecurityActionContract is Ownable, ReentrancyGuard {
    // 存储映射
    mapping(string => bool) public ipBlocked;      // IP阻断状态
    mapping(string => bool) public domainBlocked;  // 域名阻断状态

    // 治理合约地址
    address public governanceContract;

    // 事件
    event IPBlocked(string indexed ip, address executor, uint256 timestamp);
    event IPUnblocked(string indexed ip, address executor, uint256 timestamp);
    event DomainBlocked(string indexed domain, address executor, uint256 timestamp);
    event DomainUnblocked(string indexed domain, address executor, uint256 timestamp);
    event GovernanceContractUpdated(address oldGovernance, address newGovernance);

    // 修饰符
    modifier onlyAuthorized() {
        require(msg.sender == governanceContract || msg.sender == owner(), "Only authorized can call this function");
        _;
    }

    /**
     * @dev 构造函数
     * @param _governanceContract 治理合约地址
     */
    constructor(address _governanceContract) Ownable(msg.sender) {
        // 如果没有提供治理合约地址，使用部署者地址作为治理合约
        governanceContract = _governanceContract != address(0) ? _governanceContract : msg.sender;
    }

    /**
     * @dev 阻断IP地址
     * @param _ip IP地址
     */
    function blockIP(string memory _ip) 
        external 
        onlyAuthorized 
        nonReentrant 
    {
        require(bytes(_ip).length > 0, "IP cannot be empty");
        ipBlocked[_ip] = true;
        emit IPBlocked(_ip, msg.sender, block.timestamp);
    }

    /**
     * @dev 解除IP阻断
     * @param _ip IP地址
     */
    function unblockIP(string memory _ip) 
        external 
        onlyAuthorized 
        nonReentrant 
    {
        require(ipBlocked[_ip], "IP is not blocked");
        ipBlocked[_ip] = false;
        emit IPUnblocked(_ip, msg.sender, block.timestamp);
    }

    /**
     * @dev 阻断域名
     * @param _domain 域名
     */
    function blockDomain(string memory _domain) 
        external 
        onlyAuthorized 
        nonReentrant 
    {
        require(bytes(_domain).length > 0, "Domain cannot be empty");
        domainBlocked[_domain] = true;
        emit DomainBlocked(_domain, msg.sender, block.timestamp);
    }

    /**
     * @dev 解除域名阻断
     * @param _domain 域名
     */
    function unblockDomain(string memory _domain) 
        external 
        onlyAuthorized 
        nonReentrant 
    {
        require(domainBlocked[_domain], "Domain is not blocked");
        domainBlocked[_domain] = false;
        emit DomainUnblocked(_domain, msg.sender, block.timestamp);
    }

    /**
     * @dev 检查IP是否被阻断
     * @param _ip IP地址
     */
    function isIPBlocked(string memory _ip) external view returns (bool) {
        return ipBlocked[_ip];
    }

    /**
     * @dev 检查域名是否被阻断
     * @param _domain 域名
     */
    function isDomainBlocked(string memory _domain) external view returns (bool) {
        return domainBlocked[_domain];
    }

    /**
     * @dev 更新治理合约地址
     * @param _newGovernanceContract 新治理合约地址
     */
    function updateGovernanceContract(address _newGovernanceContract) external onlyOwner {
        require(_newGovernanceContract != address(0), "New governance contract cannot be zero address");
        address oldGovernance = governanceContract;
        governanceContract = _newGovernanceContract;

        emit GovernanceContractUpdated(oldGovernance, _newGovernanceContract);
    }
}

contracts/ThreatBatch.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ThreatBatch
 * @dev 高效的威胁情报批量处理合约，优化Gas使用并支持批量查询
 */
contract ThreatBatch is Ownable, ReentrancyGuard {

    struct CompactProfile {
        uint40 lastOffenseTime; // 上次违规时间 (节省空间)
        uint16 offenseCount;    // 违规次数
        uint16 riskScore;       // 风险分
    }

    mapping(string => CompactProfile) public profiles;

    // 批量事件，大大减少客户端的事件监听压力
    event PunishBatch(string[] ips, uint32[] durations);
    event ProfileUpdated(string ip, uint16 newScore, uint16 newOffenseCount, uint40 lastOffenseTime);

    // 常量定义
    uint32 public immutable TIER_1;   // 24小时
    uint32 public immutable TIER_2;   // 3天
    uint32 public immutable TIER_3;   // 7天

    // 限制单次批量操作的大小，防止Gas耗尽
    uint256 public constant MAX_BATCH_SIZE = 100;

    constructor() Ownable(msg.sender) {
        TIER_1 = 86400;   // 24小时
        TIER_2 = 259200;  // 3天
        TIER_3 = 604800;  // 7天
    }

    /**
     * @dev 批量上报威胁，极大降低交互频率
     * @param ips IP地址数组
     * @param scores 对应的风险分数
     */
    function reportBatch(string[] calldata ips, uint16[] calldata scores) external onlyOwner nonReentrant {
        require(ips.length == scores.length, "Length mismatch");
        require(ips.length > 0, "Empty batch");
        require(ips.length <= MAX_BATCH_SIZE, "Batch too large");

        uint32[] memory durations = new uint32[](ips.length);

        for (uint i = 0; i < ips.length; i++) {
            string memory ip = ips[i];
            CompactProfile storage p = profiles[ip];

            // 1. 更新分数
            uint256 newScore = uint256(p.riskScore) + uint256(scores[i]);
            require(newScore <= type(uint16).max, "Score overflow");
            p.riskScore = uint16(newScore);

            // 2. 计算阶梯与时长
            // 简单防抖：1小时内不重复计数
            if (block.timestamp > p.lastOffenseTime + 1 hours) {
                require(p.offenseCount < type(uint16).max, "Offense count overflow");
                p.offenseCount++;
                p.lastOffenseTime = uint40(block.timestamp);
            } else if (p.offenseCount == 0) {
                p.offenseCount = 1;
            }

            // 3. 判定时长
            if (p.offenseCount == 1) {
                durations[i] = TIER_1;
            } else if (p.offenseCount == 2) {
                durations[i] = TIER_2;
            } else {
                durations[i] = TIER_3;
            }

            // 发出详细更新事件
            emit ProfileUpdated(ip, p.riskScore, p.offenseCount, p.lastOffenseTime);
        }

        // 4. 发送一次批量事件
        emit PunishBatch(ips, durations);
    }

    /**
     * @dev 查询单个IP的威胁档案
     * @param ip IP地址
     * @return lastOffenseTime 上次违规时间
     * @return offenseCount 违规次数
     * @return riskScore 风险分数
     */
    function getProfile(string memory ip) external view returns (uint40, uint16, uint16) {
        CompactProfile storage p = profiles[ip];
        return (p.lastOffenseTime, p.offenseCount, p.riskScore);
    }

    /**
     * @dev 批量查询多个IP的威胁档案
     * @param ips IP地址数组
     * @return lastOffenseTimes 上次违规时间数组
     * @return offenseCounts 违规次数数组
     * @return riskScores 风险分数数组
     */
    function getProfilesBatch(string[] calldata ips) external view returns (uint40[] memory, uint16[] memory, uint16[] memory) {
        uint256 length = ips.length;
        uint40[] memory lastOffenseTimes = new uint40[](length);
        uint16[] memory offenseCounts = new uint16[](length);
        uint16[] memory riskScores = new uint16[](length);

        for (uint i = 0; i < length; i++) {
            CompactProfile storage p = profiles[ips[i]];
            lastOffenseTimes[i] = p.lastOffenseTime;
            offenseCounts[i] = p.offenseCount;
            riskScores[i] = p.riskScore;
        }

        return (lastOffenseTimes, offenseCounts, riskScores);
    }

    /**
     * @dev 获取封禁时长（基于违规次数）
     * @param offenseCount 违规次数
     * @return 封禁时长（秒）
     */
    function getBanDuration(uint16 offenseCount) external view returns (uint32) {
        if (offenseCount == 1) {
            return TIER_1;
        } else if (offenseCount == 2) {
            return TIER_2;
        } else {
            return TIER_3;
        }
    }

    /**
     * @dev 紧急清除功能，仅在极端情况下使用
     */
    function emergencyClearProfile(string memory ip) external onlyOwner {
        delete profiles[ip];
    }
}

contracts/ThreatConsensus.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title ThreatConsensus
 * @dev 带证据的威胁情报共识合约，实现乐观验证机制
 * 客户端先本地防御，再上传证据，达成共识后全网同步
 * 包含代币验证、提交-揭示机制和白名单功能
 */
contract ThreatConsensus is Ownable, ReentrancyGuard {

    constructor(address tokenAddress) Ownable(msg.sender) {
        orasrsToken = IERC20(tokenAddress);
        // 初始化默认白名单 - 直接操作映射而不是调用函数
        whitelist["8.8.8.8"] = true;
        whitelist["8.8.4.4"] = true;
        whitelist["1.1.1.1"] = true;
        whitelist["1.0.0.1"] = true;
    }

    struct AttackProof {
        address reporter;
        uint64 timestamp;
        uint8 cpuLoad;       // 攻击时CPU负载 (0-100)
        string logHash;      // 日志片段的哈希 (防篡改)
        string attackType;   // 攻击类型
    }

    struct ThreatStatus {
        bool isConfirmed;    // 是否达成全网共识
        uint256 reportCount; // 举报次数
        uint256 totalRiskScore;
        uint256 confirmedAt; // 确认时间戳
    }

    // 提交-揭示机制的数据结构
    struct Commitment {
        bytes32 hash;
        uint256 commitBlock;
        bool revealed;
    }

    mapping(string => ThreatStatus) public threatStatus;
    mapping(string => AttackProof[]) public evidenceHistory; // IP -> 证据列表
    mapping(address => mapping(string => bool)) public hasReported; // 防止同一节点重复举报
    mapping(bytes32 => Commitment) public commitments; // 提交的哈希值
    mapping(string => bool) public whitelist; // 白名单IP

    // 代币合约地址
    IERC20 public orasrsToken;
    // 代币持有门槛
    uint256 public constant MIN_TOKEN_BALANCE = 1000 * 10**18; // 1000代币

    // 触发全网封禁的阈值 (例如 3 个不同节点举报)
    uint256 public constant CONSENSUS_THRESHOLD = 3;
    uint256 public constant MAX_CPULOAD = 100;
    // 提交-揭示机制的区块延迟
    uint256 public constant REVEAL_DELAY = 10;

    event LocalDefenseActive(string indexed ip, address indexed reporter); // 仅记录，不全网广播
    event GlobalThreatConfirmed(string indexed ip, string reason);         // 全网广播，所有节点需同步
    event ThreatReportRevoked(string indexed ip, address indexed reporter); // 撤销举报事件
    event ThreatCommitted(bytes32 indexed commitment, address indexed reporter, uint256 commitBlock); // 提交事件
    event ThreatRevealed(string indexed ip, address indexed reporter, string indexed salt); // 揭示事件
    event WhitelistUpdated(string indexed ip, bool isWhitelisted); // 白名单更新事件

    /**
     * @dev 提交阶段：提交威胁证据的哈希值（防止跟风攻击）
     * @param ipHash IP地址的哈希值
     * @param salt 随机盐值
     */
    function commitThreatEvidence(bytes32 ipHash, string calldata salt) external nonReentrant {
        // 验证代币持有量
        require(orasrsToken.balanceOf(msg.sender) >= MIN_TOKEN_BALANCE, "Insufficient token balance for threat reporting");

        bytes32 commitment = keccak256(abi.encodePacked(ipHash, salt, msg.sender));

        // 确保承诺未被占用
        require(commitments[commitment].hash == bytes32(0), "Commitment already exists");

        commitments[commitment] = Commitment({
            hash: ipHash,
            commitBlock: block.number,
            revealed: false
        });

        emit ThreatCommitted(commitment, msg.sender, block.number);
    }

    /**
     * @dev 揭示阶段：揭示实际的威胁证据（在提交后一定区块数）
     * @param ip 实际IP地址
     * @param salt 之前使用的盐值
     * @param cpuLoad 攻击时CPU负载 (0-100)
     * @param logHash 日志片段的哈希
     * @param attackType 攻击类型
     * @param riskScore 风险分数
     */
    function revealThreatEvidence(
        string calldata ip, 
        string calldata salt,
        uint8 cpuLoad, 
        string calldata logHash, 
        string calldata attackType,
        uint256 riskScore
    ) external nonReentrant {
        bytes32 ipHash = keccak256(abi.encodePacked(ip));
        bytes32 commitment = keccak256(abi.encodePacked(ipHash, salt, msg.sender));

        Commitment storage commit = commitments[commitment];
        require(commit.hash != bytes32(0), "No such commitment");
        require(!commit.revealed, "Commitment already revealed");
        require(block.number >= commit.commitBlock + REVEAL_DELAY, "Reveal delay not reached");

        // 验证哈希匹配
        require(commit.hash == ipHash, "Hash mismatch");

        // 标记为已揭示
        commit.revealed = true;

        // 检查白名单
        require(!whitelist[ip], "IP is in whitelist");

        // 验证参数
        require(cpuLoad <= MAX_CPULOAD, "CPU load out of range");
        require(bytes(ip).length > 0, "IP cannot be empty");
        require(bytes(logHash).length > 0, "Log hash cannot be empty");

        ThreatStatus storage status = threatStatus[ip];

        // 检查是否已经举报过（防重复）
        require(!hasReported[msg.sender][ip], "Already reported this IP");

        // 1. 存储证据 (供治理节点或AI审计)
        evidenceHistory[ip].push(AttackProof({
            reporter: msg.sender,
            timestamp: uint64(block.timestamp),
            cpuLoad: cpuLoad,
            logHash: logHash,
            attackType: attackType
        }));

        // 2. 更新状态
        status.reportCount++;
        status.totalRiskScore += riskScore;
        hasReported[msg.sender][ip] = true;

        emit ThreatRevealed(ip, msg.sender, salt);
        emit LocalDefenseActive(ip, msg.sender); // 通知链上：有人被攻击了

        // 3. 检查是否达成共识
        if (!status.isConfirmed && status.reportCount >= CONSENSUS_THRESHOLD) {
            status.isConfirmed = true;
            status.confirmedAt = block.timestamp;
            emit GlobalThreatConfirmed(ip, attackType); // 🚀 触发全网封禁指令
        }
    }

    /**
     * @dev 撤销之前的举报（在达成共识前）
     * @param ip 要撤销举报的IP
     */
    function revokeThreatReport(string calldata ip) external {
        ThreatStatus storage status = threatStatus[ip];

        require(!status.isConfirmed, "Threat already confirmed");
        require(hasReported[msg.sender][ip], "You haven't reported this IP");

        // 减少举报计数
        status.reportCount--;
        // 注意：不减少totalRiskScore，因为无法知道之前贡献的分数

        // 标记为未举报
        hasReported[msg.sender][ip] = false;

        emit ThreatReportRevoked(ip, msg.sender);
    }

    /**
     * @dev 治理节点强制确认 (上帝模式)
     * @param ip 要强制确认的IP
     */
    function forceConfirm(string calldata ip) external onlyOwner {
        require(!whitelist[ip], "Cannot confirm whitelisted IP");
        ThreatStatus storage status = threatStatus[ip];
        status.isConfirmed = true;
        status.confirmedAt = block.timestamp;
        emit GlobalThreatConfirmed(ip, "Governance Force Block");
    }

    /**
     * @dev 治理节点强制撤销确认
     * @param ip 要撤销确认的IP
     */
    function forceRevoke(string calldata ip) external onlyOwner {
        ThreatStatus storage status = threatStatus[ip];
        require(status.isConfirmed, "Threat not confirmed");
        status.isConfirmed = false;
        status.confirmedAt = 0;
    }

    /**
     * @dev 治理节点添加白名单IP
     * @param ip 要添加到白名单的IP
     */
    function addToWhitelist(string calldata ip) public onlyOwner {
        whitelist[ip] = true;
        emit WhitelistUpdated(ip, true);
    }

    /**
     * @dev 治理节点移除白名单IP
     * @param ip 要从白名单移除的IP
     */
    function removeFromWhitelist(string calldata ip) public onlyOwner {
        whitelist[ip] = false;
        emit WhitelistUpdated(ip, false);
    }

    /**
     * @dev 检查IP是否在白名单中
     * @param ip 要检查的IP
     * @return 是否在白名单中
     */
    function isWhitelisted(string calldata ip) external view returns (bool) {
        return whitelist[ip];
    }

    /**
     * @dev 获取指定IP的证据数量
     * @param ip 目标IP
     * @return 证据数量
     */
    function getEvidenceCount(string calldata ip) external view returns (uint256) {
        return evidenceHistory[ip].length;
    }

    /**
     * @dev 获取指定IP的威胁状态
     * @param ip 目标IP
     * @return isConfirmed, reportCount, totalRiskScore, confirmedAt
     */
    function getThreatStatus(string calldata ip) external view returns (bool, uint256, uint256, uint256) {
        ThreatStatus storage status = threatStatus[ip];
        return (status.isConfirmed, status.reportCount, status.totalRiskScore, status.confirmedAt);
    }

    /**
     * @dev 检查某个地址是否已举报过某个IP
     * @param reporter 举报者地址
     * @param ip 目标IP
     * @return 是否已举报
     */
    function hasAddressReported(address reporter, string calldata ip) external view returns (bool) {
        return hasReported[reporter][ip];
    }

    /**
     * @dev 检查承诺是否已揭示
     * @param commitment 承诺哈希
     * @return 是否已揭示
     */
    function isCommitmentRevealed(bytes32 commitment) external view returns (bool) {
        return commitments[commitment].revealed;
    }

    /**
     * @dev 检查承诺是否有效（存在且未揭示）
     * @param commitment 承诺哈希
     * @return 是否有效
     */
    function isValidCommitment(bytes32 commitment) external view returns (bool) {
        Commitment storage commit = commitments[commitment];
        return (commit.hash != bytes32(0) && !commit.revealed && block.number >= commit.commitBlock + REVEAL_DELAY);
    }
}

contracts/ThreatIntelligenceCoordination.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

contract ThreatIntelligenceCoordination {
    // 威胁级别枚举
    enum ThreatLevel { Info, Warning, Critical, Emergency }

    // 威胁情报结构
    struct ThreatIntel {
        string sourceIP;
        string targetIP;
        ThreatLevel threatLevel;
        uint256 timestamp;
        string threatType;
        bool isActive;
    }

    // 存储威胁情报
    mapping(string => ThreatIntel) public threatIntels;  // IP -> ThreatIntel
    mapping(string => bool) public isThreatIP;          // IP -> 是否为威胁IP
    mapping(string => uint256) public threatScores;     // IP -> 威胁分数

    event ThreatIntelAdded(string indexed ip, ThreatLevel level, string threatType, uint256 timestamp);
    event ThreatIntelRemoved(string indexed ip, uint256 timestamp);
    event ThreatScoreUpdated(string indexed ip, uint256 score, uint256 timestamp);

    /**
     * @dev 构造函数
     */
    constructor() {
    }

    /**
     * @dev 添加威胁情报
     */
    function addThreatIntel(
        string memory _ip,
        ThreatLevel _threatLevel,
        string memory _threatType
    ) external {
        require(bytes(_ip).length > 0, "IP cannot be empty");

        threatIntels[_ip] = ThreatIntel({
            sourceIP: _ip,
            targetIP: "",
            threatLevel: _threatLevel,
            timestamp: block.timestamp,
            threatType: _threatType,
            isActive: true
        });

        isThreatIP[_ip] = true;

        emit ThreatIntelAdded(_ip, _threatLevel, _threatType, block.timestamp);
    }

    /**
     * @dev 更新威胁分数
     */
    function updateThreatScore(string memory _ip, uint256 _score) external {
        require(bytes(_ip).length > 0, "IP cannot be empty");
        threatScores[_ip] = _score;

        emit ThreatScoreUpdated(_ip, _score, block.timestamp);
    }

    /**
     * @dev 获取威胁分数
     */
    function getThreatScore(string memory _ip) external view returns (uint256) {
        return threatScores[_ip];
    }

    /**
     * @dev 移除威胁情报
     */
    function removeThreatIntel(string memory _ip) external {
        threatIntels[_ip].isActive = false;
        isThreatIP[_ip] = false;

        emit ThreatIntelRemoved(_ip, block.timestamp);
    }

    /**
     * @dev 检查IP是否为威胁IP
     */
    function isThreatSource(string memory _ip) external view returns (bool) {
        return isThreatIP[_ip] && threatIntels[_ip].isActive;
    }

    /**
     * @dev 获取威胁情报
     */
    function getThreatIntel(string memory _ip) external view returns (
        string memory sourceIP,
        string memory targetIP,
        ThreatLevel threatLevel,
        uint256 timestamp,
        string memory threatType,
        bool isActive
    ) {
        ThreatIntel memory intel = threatIntels[_ip];
        return (
            intel.sourceIP,
            intel.targetIP,
            intel.threatLevel,
            intel.timestamp,
            intel.threatType,
            intel.isActive
        );
    }

    /**
     * @dev 批量更新多个IP的威胁分数
     */
    function batchUpdateThreatScores(string[] memory _ips, uint256[] memory _scores) external {
        require(_ips.length == _scores.length, "IP and score arrays must have same length");

        for (uint i = 0; i < _ips.length; i++) {
            threatScores[_ips[i]] = _scores[i];
            emit ThreatScoreUpdated(_ips[i], _scores[i], block.timestamp);
        }
    }

    /**
     * @dev 获取多个IP的威胁分数
     */
    function getThreatScores(string[] memory _ips) external view returns (uint256[] memory) {
        uint256[] memory scores = new uint256[](_ips.length);
        for (uint i = 0; i < _ips.length; i++) {
            scores[i] = threatScores[_ips[i]];
        }
        return scores;
    }
}

contracts/ThreatStats.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title 威胁态势分析合约
 * @notice 用于分析宏观数据，提供仪表盘数据
 */
contract ThreatStats {
    // 统计每种攻击类型的总次数
    mapping(uint8 => uint256) public attackTypeCounts;

    // 全网总威胁数
    uint256 public totalThreatsDetected;

    // 最高危的 IP (简单的 Top 1 记录)
    string public topThreatIp;
    uint256 public topThreatScore;

    event GlobalStatsUpdated(uint256 total, string topIp);

    // 只有核心协调合约能调用此函数更新数据
    function updateStats(uint8 attackType, string calldata ip, uint256 newScore) external {
        // 1. 更新分类统计
        attackTypeCounts[attackType]++;

        // 2. 更新总量
        totalThreatsDetected++;

        // 3. 更新"头号公敌"
        if (newScore > topThreatScore) {
            topThreatScore = newScore;
            topThreatIp = ip;
        }

        emit GlobalStatsUpdated(totalThreatsDetected, topThreatIp);
    }

    // 获取仪表盘需要的所有统计数据
    function getDashboardStats() external view returns (
        uint256 total, 
        string memory mostDangerousIp, 
        uint256 maxScore,
        uint256[] memory typeDistribution
    ) {
        // 返回前 6 种攻击类型的统计分布
        uint256[] memory distribution = new uint256[](6);
        for(uint8 i=0; i<6; i++) {
            distribution[i] = attackTypeCounts[i];
        }

        return (totalThreatsDetected, topThreatIp, topThreatScore, distribution);
    }

    /**
     * @notice 获取指定攻击类型的统计数
     * @param attackType 攻击类型
     * @return count 统计数
     */
    function getAttackTypeCount(uint8 attackType) external view returns (uint256 count) {
        return attackTypeCounts[attackType];
    }

    /**
     * @notice 重置统计数据（治理合约权限）
     */
    function resetStats() external {
        // 此函数可由治理合约调用以重置统计数据
        totalThreatsDetected = 0;
        topThreatScore = 0;
        delete topThreatIp;

        for(uint8 i = 0; i < 6; i++) {
            attackTypeCounts[i] = 0;
        }
    }
}

